import React, { useState } from "react";


const Form = () => {

    const message = "hello";
    let inputValue;

    const update = (e)=>{
        inputValue = e.target.value
    }

    const [currentMessage, setMessage] = useState(message);

    const submit = ()=>{
        setMessage(inputValue);
    }

    return (
        <>
            <div>
                <div id="container">
                    <h3> {currentMessage} </h3>
                    <input type="text" placeholder="Enter Your Name" onChange={update}/>
                    <button id="demo" onClick={submit}> Submit </button>
                </div>
            </div>
        </>
    );
}


export default Form;
