import React from "react"

let cssStyle = {};

function Message() {
    let d = new Date();
    let hour = d.getHours();
    let greeting = "";

    if (hour <= 11) {
        // return(cssStyle.color = "blue", "Good Morning");
        greeting = "Good Moring";
        cssStyle.color = "blue";
    }
    else if (hour <= 19) {
        // return(cssStyle.color = "Yellow", "Good Afternoon");
        greeting = "Good Afternoon";
        cssStyle.color = "yellow";
    }
    else{
        // return(cssStyle.color = "green", "Good Night");
        greeting = "Good Night";
        cssStyle.color = "marron";
    }

    return {cssStyle}, greeting;
}

export default Message;
